import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterPrice, setFilterPrice] = useState(""); // State for filtering by price

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("https://fakestoreapi.com/products");
        setData(response.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  // Inline styles
  const styles = {
    container: {
      maxWidth: "1200px",
      margin: "0 auto",
      padding: "20px",
      fontFamily: "Arial, sans-serif",
    },
    filterContainer: {
      marginBottom: "20px",
    },
    productList: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
      gap: "20px",
    },
    productItem: {
      border: "1px solid #ddd",
      borderRadius: "8px",
      padding: "10px",
      textAlign: "center",
      boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
    },
    productImage: {
      maxWidth: "100%",
      height: "auto",
    },
    productTitle: {
      fontSize: "18px",
      fontWeight: "bold",
    },
    productPrice: {
      color: "#008000",
      fontWeight: "bold",
    },
  };

  // Filtered data based on price
  const filteredData = filterPrice
    ? data.filter((product) => product.price <= parseFloat(filterPrice))
    : data;

  return (
    <div style={styles.container}>
      <h1>Product List</h1>
      <div style={styles.filterContainer}>
        <label>
          Filter by Price (less than or equal to):{" "}
          <input
            type="number"
            value={filterPrice}
            onChange={(e) => setFilterPrice(e.target.value)}
            placeholder="Enter price"
          />
        </label>
      </div>
      <div style={styles.productList}>
        {filteredData.map((product) => (
          <div key={product.id} style={styles.productItem}>
            <h2 style={styles.productTitle}>{product.title}</h2>
            <img
              src={product.image}
              alt={product.title}
              style={styles.productImage}
            />
            <p style={styles.productPrice}>Price: ${product.price}</p>
            <p>{product.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
